#include <stdio.h>
#include <string.h>

#define N 100

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int n;
    char a[N + 1], b[N + 1], c[N + 1];

    scanf("%d %s %s %s", &n, a, b, c);

    int dp[n + 1][n + 1][n + 1];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= n; k++) {
                dp[i][j][k] = 1e9;
            }
        }
    }

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= n; k++) {
                if (i == 0 && j == 0 && k == 0) {
                    dp[i][j][k] = 0;
                } else if (i > 0 && j > 0 && k > 0) {
                    dp[i][j][k] = min(dp[i - 1][j][k] + (a[i] != b[i]), dp[i][j - 1][k] + (a[i] != c[i]), dp[i][j][k - 1] + (b[i] != c[i]));
                }
            }
        }
    }

    printf("%d\n", dp[n][n][n]);

    return 0;
}
