#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int min_operations(const string& a, const string& b, const string& c) {
    int n = a.size();
    vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(n + 1, vector<int>(n + 1, 0)));

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            for (int k = 1; k <= n; ++k) {
                if (a[i - 1] == b[j - 1] && b[j - 1] == c[k - 1]) {
                    dp[i][j][k] = dp[i - 1][j - 1][k - 1];
                } else {
                    dp[i][j][k] = 1 + min({dp[i - 1][j][k], dp[i][j - 1][k], dp[i][j][k - 1]});
                }
            }
        }
    }

    return dp[n][n][n];
}

int main() {
    int n;
    string a, b, c;
    cin >> n >> a >> b >> c;
    cout << min_operations(a, b, c) << endl;
    return 0;
}
