import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        String a = scanner.next();
        String b = scanner.next();
        String c = scanner.next();

        int[][] dp = new int[n + 1][n + 1];
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                dp[i][j] = 100000;
            }
        }
        dp[0][0] = 0;

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                dp[i][j] = Math.min(Math.min(dp[i - 1][j], dp[i][j - 1]), dp[Math.min(i - 1, j - 1)][Math.min(i - 1, j - 1)]) + (a.charAt(i - 1) != b.charAt(j - 1) ? 1 : 0) + (a.charAt(i - 1) != c.charAt(i - 1) ? 1 : 0);
            }
        }

        System.out.println(dp[n][n]);
    }
}
