def min_operations(A, B, C):
    n = len(A)
    dp = [[[float('inf')] * 3 for _ in range(n + 1)] for _ in range(n + 1)]

    for i in range(n + 1):
        dp[i][i][0] = dp[i][i][1] = dp[i][i][2] = 0

    for length in range(1, n):
        for i in range(n - length):
            j = i + length
            for k in range(3):
                for l in range(3):
                    if k != l:
                        dp[i][j][k] = min(dp[i][j][k], dp[i][j - 1][k] + (A[j - 1] != C[j - 1] if l == 0 else A[j - 1] != B[j - 1] if l == 1 else A[j - 1] != A[j - 1] if l == 2 else 0))
                        dp[i][j][k] = min(dp[i][j][k], dp[i + 1][j][k] + (A[i] != C[i] if l == 0 else A[i] != B[i] if l == 1 else A[i] != A[i] if l == 2 else 0))
                        dp[i][j][k] = min(dp[i][j][k], dp[i][j - 1][l] + (A[j - 1] != A[j - 1] if k == 0 else A[j - 1] != B[j - 1] if k == 1 else A[j - 1] != C[j - 1] if k == 2 else 0))
                        dp[i][j][k] = min(dp[i][j][k], dp[i + 1][j][l] + (A[i] != A[i] if k == 0 else A[i] != B[i] if k == 1 else A[i] != C[i] if k == 2 else 0))

    return min(dp[0][n - 1])

n = int(input())
A = input()
B = input()
C = input()
print(min_operations(A, B, C))
