import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        long[] a = new long[n];
        long[] b = new long[n];
        long sumA = 0;
        long sumB = 0;
        long sumA1 = 0;
        long sumB1 = 0;

        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextLong();
            b[i] = scanner.nextLong();
            sumA += a[i];
            sumB += b[i];
            if (i % 2 == 0) {
                sumA1 += a[i];
                sumB1 += b[i];
            } else {
                sumA1 += b[i];
                sumB1 += a[i];
            }
        }

        long ans = (sumA - sumB1) - (sumB - sumA1);
        System.out.println(ans);
    }
}
