#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> d(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> d[i];
    }

    std::vector<int> forward(n);
    forward[0] = d[0];
    for (int i = 1; i < n; ++i) {
        forward[i] = std::max(forward[i - 1], d[i]);
    }

    std::vector<int> backward(n);
    backward[n - 1] = d[n - 1];
    for (int i = n - 2; i >= 0; --i) {
        backward[i] = std::max(backward[i + 1], d[i]);
    }

    bool canJump = true;
    for (int i = 0; i < n - 1; ++i) {
        if (forward[i] < d[i + 1] || backward[i + 1] < d[i]) {
            canJump = false;
            break;
        }
    }

    if (canJump) {
        std::cout << "yes" << std::endl;
    } else {
        std::cout << "no" << std::endl;
    }

    return 0;
}
