#include <iostream>
#include <vector>
#include <map>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, m;
vector<int> g[N];
map<pair<int, int>, int> mp;
int par[N];

void dfs(int u) {
    for (int v : g[u]) {
        if (par[v] == 0) {
            par[v] = u;
            dfs(v);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 1; i <= n + m - 1; i++) {
        int u, v;
        cin >> u >> v;
        mp[{u, v}] = i;
        g[i].push_back(u);
        g[i].push_back(v);
    }

    for (auto& [key, value] : mp) {
        int u = key.first, v = key.second;
        if (par[u] == 0) {
            par[u] = v;
            dfs(u);
        }
    }

    for (int i = 1; i <= n; i++) {
        if (par[i] == 0) {
            par[i] = i;
        }
        cout << par[i] << '\n';
    }

    return 0;
}
