#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define N 100005
#define M 100005

typedef struct {
    int to, next, w;
} Edge;

Edge edge[M * 2];
int head[N], tot;
int dfn[N], low[N], num, cnt;
int vis[N], in[N];
int dep[N], siz[N];
int fa[N][20];

void add(int u, int v, int w) {
    edge[tot].to = v;
    edge[tot].next = head[u];
    edge[tot].w = w;
    head[u] = tot++;
}

void tarjan(int u, int f) {
    dfn[u] = low[u] = ++num;
    vis[u] = 1;
    in[u] = 1;
    int i, cnt = 0;
    for (i = head[u]; i != -1; i = edge[i].next) {
        int v = edge[i].to;
        if (v == f) continue;
        if (dfn[v]) {
            low[u] = min(low[u], dfn[v]);
        } else {
            tarjan(v, u);
            low[u] = min(low[u], low[v]);
            cnt++;
            if (low[v] >= dfn[u]) {
                cnt++;
                in[u] = 0;
            }
        }
    }
    if (in[u] && cnt == 1) {
        cnt = 0;
        for (i = head[u]; i != -1; i = edge[i].next) {
            int v = edge[i].to;
            if (v != f) {
                cnt += edge[i].w;
                siz[v] = 1;
                dep[v] = dep[u] + 1;
                fa[v][0] = u;
                for (int j = 0; j < 20; j++) {
                    fa[v][j + 1] = fa[fa[v][j]][j];
                }
            }
        }
    }
}

int lca(int u, int v) {
    if (dep[u] < dep[v]) {
        int t = u;
        u = v;
        v = t;
    }
    for (int i = 19; i >= 0; i--) {
        if (dep[fa[u][i]] >= dep[v]) {
            u = fa[u][i];
        }
    }
    if (u == v) return u;
    for (int i = 19; i >= 0; i--) {
        if (fa[u][i] != fa[v][i]) {
            u = fa[u][i];
            v = fa[v][i];
        }
    }
    return fa[u][0];
}

int main() {
    int n, m;
    scanf("%d%d", &n, &m);
    memset(head, -1, sizeof(head));
    tot = 0;
    memset(dfn, 0, sizeof(dfn));
    memset(low, 0, sizeof(low));
    memset(vis, 0, sizeof(vis));
    memset(in, 0, sizeof(in));
    memset(dep, 0, sizeof(dep));
    memset(siz, 0, sizeof(siz));
    memset(fa, 0, sizeof(fa));
    for (int i = 1; i <= n; i++) {
        scanf("%d", &edge[i + m].w);
    }
    for (int i = 1; i <= m; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        add(u, v, w);
        add(v, u, w);
        edge[i + m].to = i;
        edge[i + m].next = head[i];
        head[i] = i + m;
    }
    tarjan(1, 0);
    int ans = 0;
    for (int i = 1; i <= m; i++) {
        int u = edge[i + m].to;
        int v = lca(edge[i].to, edge[i].next);
        if (dep[edge[i].to] > dep[v]) {
            ans += edge[i].w;
        }
    }
    printf("%d\n", ans);
    return 0;
}
