#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, m;
int x[N], y[N];
vector<int> g[N];
int sz[N], vis[N];

void dfs(int u) {
    vis[u] = 1;
    sz[u] = 1;
    for (int v : g[u]) {
        if (!vis[v]) {
            dfs(v);
            sz[u] += sz[v];
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 1; i <= n; i++) {
        cin >> x[i];
    }
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v >> y[i];
        g[u].push_back(v);
        g[v].push_back(u);
    }

    int ans = 0;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v >> y[i];
        if (sz[u] + x[u] < y[i]) {
            ans++;
        } else {
            dfs(u);
            if (sz[v] + x[v] < y[i]) {
                ans++;
            } else {
                dfs(v);
            }
        }
    }

    cout << ans << '\n';

    return 0;
}
