import java.io.*;
import java.util.*;

public class Main {
    static int N, M;
    static int[] X, Y, A, B;
    static int[] parent;
    static int[] size;
    static int[] minWeight;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        X = new int[N];
        Y = new int[M];
        A = new int[M];
        B = new int[M];
        parent = new int[N];
        size = new int[N];
        minWeight = new int[N];
        for (int i = 0; i < N; i++) {
            parent[i] = i;
            size[i] = 1;
            minWeight[i] = Integer.MAX_VALUE;
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            X[i] = Integer.parseInt(st.nextToken());
        }
        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            A[i] = Integer.parseInt(st.nextToken()) - 1;
            B[i] = Integer.parseInt(st.nextToken()) - 1;
            Y[i] = Integer.parseInt(st.nextToken());
        }
        Arrays.sort(Y, 0, M);
        int[] order = new int[M];
        for (int i = 0; i < M; i++) {
            order[Y[i] - 1] = i;
        }
        int ans = 0;
        for (int i = 0; i < M; i++) {
            int u = A[order[i]];
            int v = B[order[i]];
            if (find(u) != find(v)) {
                ans += dfs(u, v, X[u] + X[v] - Y[order[i]]);
                union(u, v);
            }
        }
        System.out.println(ans);
    }

    static int find(int x) {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    static void union(int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            if (size[x] < size[y]) {
                int temp = x;
                x = y;
                y = temp;
            }
            parent[y] = x;
            size[x] += size[y];
            minWeight[x] = Math.min(minWeight[x], minWeight[y]);
        }
    }

    static int dfs(int u, int v, int w) {
        int ans = 0;
        if (u == v) {
            return 1;
        }
        if (u != v) {
            int p = find(u);
            int q = find(v);
            if (p != q) {
                if (size[p] < size[q]) {
                    int temp = p;
                    p = q;
                    q = temp;
                }
                if (X[p] + X[q] < w) {
                    ans = 1;
                } else {
                    ans = dfs(p, q, w);
                }
                union(p, q);
            }
        }
        return ans;
    }
}
