#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 200005
#define K 1000005

typedef struct {
    int color, value;
} Jewel;

int n, k;
Jewel jewels[N];
int cnt[K + 1];
long long sum[K + 1];

int main() {
    scanf("%d %d", &n, &k);
    for (int i = 1; i <= n; i++) {
        scanf("%d %d", &jewels[i].color, &jewels[i].value);
    }
    for (int i = 1; i <= n; i++) {
        cnt[jewels[i].color]++;
    }
    for (int i = 1; i <= k; i++) {
        sum[i] = sum[i - 1] + (cnt[i] >= 2 ? cnt[i] * (cnt[i] - 1) / 2 : 0);
    }
    for (int i = 1; i <= n; i++) {
        long long ans = 0;
        if (i % 2 == 0) {
            int j = i / 2;
            for (int c = 1; c <= k; c++) {
                if (cnt[c] >= 2) {
                    int t = (cnt[c] - 2) / 2;
                    if (t >= j) {
                        ans = max(ans, (long long)j * jewels[c].value);
                        j = 0;
                    } else {
                        ans = max(ans, (long long)(t + 1) * jewels[c].value + (long long)(j - t - 1) * (j - t - 1) / 2 * jewels[c].value);
                        j -= t + 1;
                    }
                }
            }
        }
        printf("%lld\n", (ans == 0 ? -1 : ans));
    }
    return 0;
}
