#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <sstream>

using namespace std;

int main() {
    int N, K;
    cin >> N >> K;

    vector<int> C(N);
    vector<int> V(N);
    for (int i = 0; i < N; i++) {
        cin >> C[i] >> V[i];
    }

    vector<int> count(K + 1, 0);
    for (int i = 0; i < N; i++) {
        count[C[i]]++;
    }

    vector<int> sum(K + 1, 0);
    for (int i = 0; i < N; i++) {
        sum[C[i]] += V[i];
    }

    vector<int> dp(N + 1, 0);
    for (int i = 1; i <= N; i++) {
        if (count[i] >= 2) {
            dp[i] = max(dp[i], sum[i] + dp[i - 2]);
        }
        for (int j = 1; j <= K; j++) {
            if (i - j >= 0 && count[j] >= 2) {
                dp[i] = max(dp[i], sum[j] + dp[i - j - 2]);
            }
        }
    }

    for (int i = 1; i <= N; i++) {
        cout << (dp[i] == 0 ? -1 : dp[i]) << endl;
    }

    return 0;
}
