#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define MAX_N 100005
#define MAX_M 100005

typedef struct {
    int t, d;
} Sushi;

int cmp(const void *a, const void *b) {
    Sushi *x = (Sushi *)a;
    Sushi *y = (Sushi *)b;
    return x->d - y->d;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    Sushi s[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &s[i].t, &s[i].d);
    }

    qsort(s, n, sizeof(Sushi), cmp);

    int t[MAX_M] = {0};
    for (int i = 0; i < n; i++) {
        t[s[i].t]++;
    }

    long long ans = 0;
    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (cnt < k) {
            ans += s[i].d;
            t[s[i].t]--;
            cnt++;
        } else {
            int mx = 0;
            for (int j = 1; j <= n; j++) {
                if (t[j] > 0) {
                    mx = j;
                }
            }
            if (mx > s[i].t) {
                ans += s[i].d;
                t[s[i].t]--;
                t[mx]++;
            }
        }
    }

    int x = 0;
    for (int i = 1; i <= n; i++) {
        if (t[i] > 0) {
            x++;
        }
    }

    ans += (long long)x * x;

    printf("%lld\n", ans);

    return 0;
}
