#include <iostream>
#include <vector>
#include <algorithm>
#include <map>
#include <set>
#include <string>
#include <cmath>

using namespace std;

struct Sushi {
    int topping, deliciousness;
};

bool compareSushi(const Sushi& a, const Sushi& b) {
    return a.deliciousness > b.deliciousness;
}

int main() {
    int n, k;
    cin >> n >> k;

    vector<Sushi> sushi(n);
    for (int i = 0; i < n; i++) {
        cin >> sushi[i].topping >> sushi[i].deliciousness;
    }

    sort(sushi.begin(), sushi.end(), compareSushi);

    map<int, set<int>> toppingMap;
    for (int i = 0; i < n; i++) {
        toppingMap[sushi[i].topping].insert(i);
    }

    long long maxDeliciousness = 0;
    long long maxVarietyBonus = 0;
    for (int i = 0; i < k; i++) {
        int topping = sushi[i].topping;
        int deliciousness = sushi[i].deliciousness;

        maxDeliciousness += deliciousness;

        set<int>& indices = toppingMap[topping];
        indices.erase(indices.find(i));

        if (indices.size() > maxVarietyBonus) {
            maxVarietyBonus = indices.size();
        }
    }

    long long maxSatisfaction = maxDeliciousness + maxVarietyBonus * maxVarietyBonus;
    cout << maxSatisfaction << endl;

    return 0;
}
