#include <stdio.h>
#include <stdlib.h>

#define N 100000

int main() {
    int n;
    scanf("%d", &n);

    long long a[N], b[N];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%lld", &b[i]);
    }

    long long sum_a = 0;
    for (int i = 0; i < n; i++) {
        sum_a += a[i];
    }

    long long sum_b = 0;
    for (int i = 0; i < n; i++) {
        sum_b += b[i];
    }

    if (sum_a < sum_b) {
        printf("-1\n");
        return 0;
    }

    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] < b[i]) {
            long long diff = b[i] - a[i];
            if (sum_a - a[i] >= diff) {
                sum_a -= a[i];
                sum_a += b[i];
                a[i] = b[i];
                cnt++;
            } else {
                printf("-1\n");
                return 0;
            }
        }
    }

    printf("%d\n", cnt);
    return 0;
}
