import java.io.*;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        long[] a = new long[n + 1];
        long[] b = new long[n + 1];
        StringTokenizer st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            a[i] = Long.parseLong(st.nextToken());
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            b[i] = Long.parseLong(st.nextToken());
        }
        long sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += a[i];
        }
        long min = 0;
        long max = 0;
        for (int i = 1; i <= n; i++) {
            max += b[i];
            min = Math.min(min, b[i]);
        }
        if (max > sum) {
            pw.println(-1);
            pw.close();
            return;
        }
        long ans = 0;
        for (int i = 1; i <= n; i++) {
            if (a[i] < b[i]) {
                long diff = b[i] - a[i];
                if (sum - a[i] >= diff) {
                    sum -= a[i];
                    sum += b[i];
                    a[i] = b[i];
                    ans++;
                } else {
                    long minDiff = b[i] - min;
                    if (sum - a[i] >= minDiff) {
                        sum -= a[i];
                        sum += min;
                        a[i] = min;
                        ans++;
                    } else {
                        pw.println(-1);
                        pw.close();
                        return;
                    }
                }
            }
        }
        pw.println(ans);
        pw.close();
    }
}
