#include <iostream>
#include <vector>

int main() {
    int H, N;
    std::cin >> H >> N;

    std::vector<std::vector<int>> grid(H, std::vector<int>(4, 0));
    for (int i = 0; i < N; ++i) {
        int x, y;
        std::cin >> x >> y;
        grid[y][x] = 1;
    }

    int count = 0;
    for (int i = 0; i < H; ++i) {
        for (int j = 0; j < 4; ++j) {
            if (grid[i][j] == 0) {
                int left = j;
                while (left > 0 && grid[i][left - 1] == 0) {
                    --left;
                }
                int right = j;
                while (right < 3 && grid[i][right + 1] == 0) {
                    ++right;
                }
                if (right - left + 1 > 0) {
                    count += (right - left + 1) / 2;
                }
            }
        }
    }

    std::cout << count << std::endl;

    return 0;
}
