#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 1005

typedef long long ll;

ll n, m, a[MAXN], b[MAXN], c[MAXN], d[MAXN], f[MAXN][MAXN], g[MAXN][MAXN];

int main() {
    scanf("%lld %lld", &n, &m);
    for (int i = 1; i <= n; i++) scanf("%lld", &a[i]);
    for (int i = 1; i <= m; i++) scanf("%lld", &b[i]);

    for (int i = 1; i <= n; i++) c[i] = a[i];
    for (int i = 1; i <= m; i++) d[i] = b[i];

    qsort(c + 1, n, sizeof(ll), cmp);
    qsort(d + 1, m, sizeof(ll), cmp);

    for (int i = 1; i <= n; i++) f[i][0] = 1;
    for (int j = 1; j <= m; j++) g[0][j] = 1;

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            f[i][j] = (f[i - 1][j] + g[i][j - 1]) % MOD;
            if (c[i] == d[j]) f[i][j] = (f[i][j] + MOD - g[i - 1][j - 1]) % MOD;
            g[i][j] = (g[i - 1][j] + f[i][j]) % MOD;
        }
    }

    printf("%lld\n", f[n][m]);

    return 0;
}

int cmp(const void *a, const void *b) {
    return (*(ll *)a - *(ll *)b);
}
