#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int n, m;
    cin >> n >> m;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> b(m);
    for (int i = 0; i < m; i++) {
        cin >> b[i];
    }

    sort(a.rbegin(), a.rend());
    sort(b.rbegin(), b.rend());

    vector<vector<int>> dp(n + 1, vector<int>(m + 1));
    dp[0][0] = 1;

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            for (int k = 0; k <= i; k++) {
                for (int l = 0; l <= j; l++) {
                    if (a[i] > b[j] || a[i] > a[k] || b[j] > b[l]) {
                        continue;
                    }
                    dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[k][l]) % MOD;
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= m; j++) {
            ans = (ans + dp[i][j]) % MOD;
        }
    }

    cout << ans << endl;

    return 0;
}
