#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 200005

int main() {
    int n, i, j, k;
    long long d, a[N], b[N], c[N], ans = 0;
    scanf("%d %lld", &n, &d);
    for (i = 1; i <= n; i++) {
        scanf("%lld", &a[i]);
        b[i] = a[i];
    }
    qsort(b + 1, n, sizeof *b, cmp);
    for (i = 1; i <= n; i++) {
        c[i] = c[i - 1] + b[i];
    }
    for (i = 1; i <= n - 1; i++) {
        ans += (long long)abs(i - (n - i)) * d + b[i] + b[n - i + 1];
    }
    for (i = 1; i <= n - 1; i++) {
        ans = min(ans, c[i - 1] + c[n - i] + (long long)(n - i - 1) * d * 2);
    }
    printf("%lld\n", ans);
    return 0;
}

int cmp(const void *a, const void *b) {
    return (*(long long *)a - *(long long *)b);
}

long long min(long long a, long long b) {
    return a < b ? a : b;
}
