#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

long long solve(vector<int>& A, int D) {
    int n = A.size();
    long long res = 0;
    for (int i = 0; i < n - 1; i++) {
        res += abs(i - (n - 1)) * D + A[i] + A[n - 1];
    }
    for (int i = 0; i < n - 1; i++) {
        res = min(res, solve(A, D, i, n - 1));
    }
    return res;
}

long long solve(vector<int>& A, int D, int l, int r) {
    if (l + 1 >= r) return 0;
    int m = (l + r) / 2;
    long long res = 0;
    for (int i = l; i < m; i++) {
        res += abs(i - l) * D + A[i] + A[l];
    }
    for (int i = m; i < r; i++) {
        res += abs(i - m) * D + A[i] + A[m];
    }
    long long minRes = 1e18;
    for (int i = l; i <= m; i++) {
        minRes = min(minRes, solve(A, D, i, m - 1) + solve(A, D, m, r));
    }
    return min(res, minRes);
}

int main() {
    int n, D;
    cin >> n >> D;
    vector<int> A(n);
    for (int i = 0; i < n; i++) {
        cin >> A[i];
    }
    sort(A.begin(), A.end());
    cout << solve(A, D) << endl;
    return 0;
}
