import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        long d = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        Arrays.sort(a);
        long ans = 0;
        for (int i = 1; i < n; i++) {
            ans += (i - 1) * d + a[i] + a[i - 1];
        }
        long min = Long.MAX_VALUE;
        for (int i = 1; i < n - 1; i++) {
            long cost = (i - 1) * d + a[i] + a[i - 1] + (n - i - 1) * d + a[n - 1] + a[i];
            min = Math.min(min, cost);
        }
        ans = Math.min(ans, min);
        out.println(ans);
        out.close();
    }
}
