#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int H, W, K;
    cin >> H >> W >> K;

    long long ans = 0;
    for (int i = 0; i <= K; i++) {
        int n = K - i;
        long long c = 1;
        for (int j = 1; j <= n; j++) c = c * (H + W - j) % MOD;
        for (int j = 1; j <= i; j++) c = c * j % MOD;
        ans = (ans + c * power(i + n, MOD - 2) % MOD * power(i, MOD - 2) % MOD * power(n, MOD - 2) % MOD) % MOD;
    }

    cout << ans << endl;
    return 0;
}
