#include <iostream>
using namespace std;

int main() {
    int n, h, w;
    cin >> n >> h >> w;

    long long ans = 1;
    for (int i = 1; i <= h; i++) {
        ans *= (n - i + 1);
    }
    for (int i = 1; i <= w; i++) {
        ans /= (h - i + 1);
    }
    cout << ans << endl;

    return 0;
}
