#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100
#define MAX_P 20

int main() {
    int n, a, b, count = 0;
    int p[MAX_N];
    int i, j, k;

    scanf("%d", &n);
    scanf("%d %d", &a, &b);

    for (i = 0; i < n; i++) {
        scanf("%d", &p[i]);
    }

    qsort(p, n, sizeof(int), compare);

    for (i = 0; i < n; i++) {
        if (p[i] <= a) {
            for (j = i + 1; j < n; j++) {
                if (p[j] >= a + 1 && p[j] <= b) {
                    for (k = j + 1; k < n; k++) {
                        if (p[k] >= b + 1) {
                            count++;
                            i = k;
                            j = k;
                            break;
                        }
                    }
                    break;
                }
            }
        }
    }

    printf("%d\n", count);

    return 0;
}

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}
