#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 405
#define MAX_W 405

int H, W;
char grid[MAX_H][MAX_W];
int visited[MAX_H][MAX_W];
int black[MAX_H][MAX_W];
int white[MAX_H][MAX_W];

int dfs(int x, int y, int color) {
    if (x < 0 || x >= H || y < 0 || y >= W || visited[x][y] || grid[x][y] != color) {
        return 0;
    }
    visited[x][y] = 1;
    int count = 1;
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            if (i == 0 && j == 0) continue;
            count += dfs(x + i, y + j, (color == '#' ? '.' : '#'));
        }
    }
    return count;
}

int main() {
    scanf("%d %d", &H, &W);
    for (int i = 0; i < H; i++) {
        scanf("%s", grid[i]);
        for (int j = 0; j < W; j++) {
            if (grid[i][j] == '#') {
                black[i][j] = dfs(i, j, '#');
            } else {
                white[i][j] = dfs(i, j, '.');
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (grid[i][j] == '#') {
                for (int k = 0; k < H; k++) {
                    for (int l = 0; l < W; l++) {
                        if (grid[k][l] == '.' && k != i && l != j) {
                            ans += black[i][j] * white[k][l];
                        }
                    }
                }
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
