#include <iostream>
#include <vector>
#include <queue>
#include <cstring>

using namespace std;

const int MAX_H = 405;
const int MAX_W = 405;

int H, W;
char grid[MAX_H][MAX_W];
int dist[MAX_H][MAX_W];
int blackCount, whiteCount;
vector<pair<int, int>> black, white;

void bfs(int x, int y) {
    queue<pair<int, int>> q;
    q.push({x, y});
    dist[x][y] = 0;
    while (!q.empty()) {
        int cx = q.front().first;
        int cy = q.front().second;
        q.pop();
        if (grid[cx][cy] == '#') {
            for (int i = -1; i <= 1; i++) {
                for (int j = -1; j <= 1; j++) {
                    if (i == 0 && j == 0) continue;
                    int nx = cx + i;
                    int ny = cy + j;
                    if (nx < 0 || ny < 0 || nx >= H || ny >= W) continue;
                    if (dist[nx][ny] == -1 && grid[nx][ny] == '.') {
                        dist[nx][ny] = dist[cx][cy] + 1;
                        q.push({nx, ny});
                    }
                }
            }
        } else {
            for (int i = -1; i <= 1; i++) {
                for (int j = -1; j <= 1; j++) {
                    if (i == 0 && j == 0) continue;
                    int nx = cx + i;
                    int ny = cy + j;
                    if (nx < 0 || ny < 0 || nx >= H || ny >= W) continue;
                    if (dist[nx][ny] == -1 && grid[nx][ny] == '#') {
                        dist[nx][ny] = dist[cx][cy] + 1;
                        q.push({nx, ny});
                    }
                }
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> H >> W;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            cin >> grid[i][j];
            if (grid[i][j] == '#') {
                black.push_back({i, j});
                blackCount++;
            } else {
                white.push_back({i, j});
                whiteCount++;
            }
        }
    }

    memset(dist, -1, sizeof(dist));
    for (int i = 0; i < black.size(); i++) {
        bfs(black[i].first, black[i].second);
    }

    int ans = 0;
    for (int i = 0; i < black.size(); i++) {
        for (int j = 0; j < white.size(); j++) {
            if (dist[black[i].first][black[i].second] + dist[white[j].first][white[j].second] % 2 == 0 && dist[black[i].first][black[i].second] + dist[white[j].first][white[j].second] > 0) {
                ans++;
            }
        }
    }

    cout << ans << '\n';

    return 0;
}
