from collections import deque

def bfs(grid, start, end):
    rows, cols = len(grid), len(grid[0])
    visited = [[False]*cols for _ in range(rows)]
    queue = deque([(start, 0)])
    visited[start[0]][start[1]] = True

    while queue:
        (x, y), step = queue.popleft()
        if (x, y) == end:
            return step
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + 2*dx, y + 2*dy
            if 0 <= nx < rows and 0 <= ny < cols and not visited[nx][ny] and grid[nx][ny] == '#':
                queue.append(((nx, ny), step+1))
                visited[nx][ny] = True
            if 0 <= x + dx < rows and 0 <= y + dy < cols and not visited[x + dx][y + dy] and grid[x + dx][y + dy] == '.':
                queue.append(((x + dx, y + dy), step+1))
                visited[x + dx][y + dy] = True
    return -1

def count_pairs(grid):
    rows, cols = len(grid), len(grid[0])
    count = 0
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == '#':
                for k in range(rows):
                    for m in range(cols):
                        if grid[k][m] == '.' and bfs(grid, (i, j), (k, m)) != -1:
                            count += 1
    return count

H, W = map(int, input().split())
grid = [input() for _ in range(H)]
print(count_pairs(grid))
