#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100000
#define Q 100000

int main() {
    int n, q;
    int a[N + 1];
    int x[Q + 1];
    int ans[Q + 1];
    int i, j, k, l, r, m, p, cnt;

    scanf("%d %d", &n, &q);
    for (i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    for (i = 1; i <= q; i++) {
        scanf("%d", &x[i]);
    }

    for (i = 1; i <= q; i++) {
        l = 1;
        r = n;
        while (l <= r) {
            m = (l + r) / 2;
            if (a[m] >= x[i]) {
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        p = l;
        cnt = 0;
        for (j = p + 1; j <= n; j++) {
            if (abs(a[j] - x[i]) <= abs(a[p] - x[i])) {
                cnt++;
                p = j;
            }
        }
        ans[i] = 0;
        for (j = 1; j <= p; j++) {
            ans[i] += a[j];
        }
        for (j = p + 1; j <= n - cnt; j++) {
            ans[i] += a[j];
        }
        for (j = n - cnt + 1; j <= n; j++) {
            ans[i] += a[j] - x[i];
        }
    }

    for (i = 1; i <= q; i++) {
        printf("%d\n", ans[i]);
    }

    return 0;
}
