#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, q;
    cin >> n >> q;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> x(q);
    for (int i = 0; i < q; i++) {
        cin >> x[i];
    }

    vector<long long> ans(q);

    for (int i = 0; i < q; i++) {
        int x_i = x[i];
        int l = 0, r = n - 1;
        while (l < r) {
            int mid = (l + r) / 2;
            if (a[mid] >= x_i) r = mid;
            else l = mid + 1;
        }

        int idx = l;
        long long sum = 0;
        while (idx < n) {
            sum += a[idx];
            int left = idx + 1, right = n - 1;
            while (left < right) {
                int mid = (left + right + 1) / 2;
                if (a[mid] <= x_i) left = mid;
                else right = mid - 1;
            }
            idx = left + 1;
        }

        ans[i] = sum;
    }

    for (int i = 0; i < q; i++) {
        cout << ans[i] << endl;
    }

    return 0;
}
