import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        int q = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long[] x = new long[q];
        for (int i = 0; i < q; i++) {
            x[i] = sc.nextLong();
        }
        long[] ans = new long[q];
        for (int i = 0; i < q; i++) {
            int idx = Arrays.binarySearch(a, x[i]);
            if (idx < 0) idx = -idx - 1;
            int l = idx - 1, r = n - idx;
            long sum = 0;
            if (l >= 0) {
                int j = l;
                while (j >= 0 && a[j] > x[i]) {
                    sum += a[j];
                    j--;
                }
            }
            if (r > 0) {
                int j = r - 1;
                while (j >= 0 && a[n - j - 1] > x[i]) {
                    sum += a[n - j - 1];
                    j--;
                }
            }
            ans[i] = sum;
        }
        for (long i : ans) {
            System.out.println(i);
        }
    }
}
