#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 5005

int n, a[N], d[N], f[N], g[N], h[N], e[N][N], vis[N], cnt;

void dfs(int u) {
    vis[u] = 1;
    f[u] = 1;
    g[u] = a[u];
    for (int v = 1; v <= n; v++) {
        if (e[u][v] && !vis[v]) {
            dfs(v);
            f[u] += f[v];
            g[u] += g[v];
            if (g[v] > 0) {
                g[u] = 0;
            }
        }
    }
    if (g[u] > 0) {
        h[u] = 1;
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        e[u][v] = e[v][u] = 1;
    }
    for (int i = 1; i <= n; i++) {
        d[i] = 1;
        for (int j = 1; j <= n; j++) {
            d[i] += e[i][j];
        }
    }
    for (int i = 1; i <= n; i++) {
        if (!h[i]) {
            dfs(i);
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (e[i][j] && f[i] + f[j] == d[i] + d[j] - 1 && h[i] + h[j] == 0) {
                ans++;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
