#include <stdio.h>
#include <stdlib.h>

#define MAX 105

int main() {
    int w, h, n, x, y, i, j, k, cost, min_cost;
    int enemy[MAX][MAX];

    scanf("%d %d %d", &w, &h, &n);
    for (i = 0; i < n; i++) {
        scanf("%d %d", &x, &y);
        enemy[x][y] = 1;
    }

    min_cost = 0;
    for (i = 0; i < w; i++) {
        for (j = 0; j < h; j++) {
            if (enemy[i][j] == 1) {
                cost = 0;
                for (k = 0; k < w; k++) {
                    if (enemy[k][j] == 1) {
                        cost++;
                    }
                }
                for (k = 0; k < h; k++) {
                    if (enemy[i][k] == 1) {
                        cost++;
                    }
                }
                min_cost += cost - 1;
            }
        }
    }

    printf("%d\n", min_cost);
    return 0;
}
