#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAXN = 5005;

int n, a[MAXN], d[MAXN], f[MAXN];
vector<int> e[MAXN];

void dfs(int u) {
    f[u] = 1;
    for (int v : e[u]) {
        if (!f[v]) {
            d[v] = d[u] + 1;
            dfs(v);
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
        e[v].push_back(u);
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        if (f[i]) continue;
        d[i] = 0;
        dfs(i);
        long long sum = 0;
        for (int j = 1; j <= n; j++) {
            if (d[j] == d[i]) {
                sum += a[j];
            }
        }
        if (sum > 0) {
            ans = max(ans, d[i]);
        }
    }

    cout << n - 1 - ans << '\n';

    return 0;
}
