from collections import defaultdict
import sys

def dfs(node, parent, graph, visited, a):
    visited[node] = True
    for child in graph[node]:
        if child != parent:
            if not visited[child]:
                dfs(child, node, graph, visited, a)
            if a[child] < 0:
                a[node] += a[child]
    return a

def solve():
    n = int(input())
    a = list(map(int, input().split()))
    graph = defaultdict(list)
    for _ in range(n - 1):
        u, v = map(int, input().split())
        graph[u].append(v)
        graph[v].append(u)
    visited = [False] * (n + 1)
    a = [0] + a
    ans = 0
    for i in range(1, n + 1):
        if not visited[i]:
            a = dfs(i, -1, graph, visited, a)
            if a[i] > 0:
                ans += 1
    print(n - 1 - ans)

if __name__ == "__main__":
    solve()
