#include <stdio.h>
#include <stdlib.h>

#define N 100005
#define K 105

int main() {
    int n, k;
    int h[N];
    long long dp[N][K];

    scanf("%d %d", &n, &k);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &h[i]);
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= k && i + j <= n; j++) {
            dp[i][j] = dp[i][j - 1] + abs(h[i] - h[i + j]);
            if (i > 1) {
                for (int l = 1; l <= k && i + j + l <= n; l++) {
                    dp[i][j] = dp[i][j] < dp[i - 1][l] + abs(h[i - 1] - h[i + j]) + abs(h[i + j] - h[i + j + l]) ? dp[i][j] : dp[i - 1][l] + abs(h[i - 1] - h[i + j]) + abs(h[i + j] - h[i + j + l]);
                }
            }
        }
    }

    printf("%lld\n", dp[n][k]);

    return 0;
}
