import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int k = scanner.nextInt();
        int[] h = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            h[i] = scanner.nextInt();
        }
        long[][] dp = new long[n + 1][k + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= k; j++) {
                dp[i][j] = Long.MAX_VALUE;
            }
        }
        for (int i = 1; i <= n; i++) {
            dp[i][1] = Math.abs(h[i] - h[i - 1]);
        }
        for (int i = 2; i <= n; i++) {
            for (int j = 2; j <= k; j++) {
                for (int l = i; l <= Math.min(i + k, n); l++) {
                    dp[l][j] = Math.min(dp[l][j], dp[i - 1][j - 1] + Math.abs(h[l] - h[i - 1]));
                }
            }
        }
        long ans = Long.MAX_VALUE;
        for (int i = k; i <= n; i++) {
            ans = Math.min(ans, dp[i][k]);
        }
        System.out.println(ans);
    }
}
