#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

struct Node {
    int x, y, cost;
};

int main() {
    int w, h, n;
    cin >> w >> h >> n;

    vector<vector<int>> grid(h, vector<int>(w, 0));
    vector<Node> enemies(n);

    for (int i = 0; i < n; i++) {
        int x, y;
        cin >> x >> y;
        enemies[i] = {x, y, 0};
        grid[y][x] = 1;
    }

    int ans = 1e9;
    for (int i = 0; i < n; i++) {
        int x = enemies[i].x;
        int y = enemies[i].y;
        int cost = 0;

        for (int j = 0; j < h; j++) {
            for (int k = 0; k < w; k++) {
                if (grid[j][k] == 1) {
                    cost += abs(k - x) + abs(j - y);
                    grid[j][k] = 0;
                }
            }
        }

        queue<pair<int, int>> q;
        vector<vector<bool>> visited(h, vector<bool>(w, false));
        q.push({x, y});
        visited[y][x] = true;

        while (!q.empty()) {
            int x = q.front().first;
            int y = q.front().second;
            q.pop();

            for (int i = -1; i <= 1; i++) {
                for (int j = -1; j <= 1; j++) {
                    int nx = x + i;
                    int ny = y + j;

                    if (nx >= 0 && nx < w && ny >= 0 && ny < h && !visited[ny][nx]) {
                        q.push({nx, ny});
                        visited[ny][nx] = true;
                    }
                }
            }
        }

        for (int j = 0; j < h; j++) {
            for (int k = 0; k < w; k++) {
                if (grid[j][k] == 1) {
                    cost = 1e9;
                    break;
                }
            }
        }

        ans = min(ans, cost);
    }

    cout << ans << endl;

    return 0;
}
