#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAX_N 100005

int main() {
    int n;
    scanf("%d", &n);

    int a[MAX_N], b[MAX_N], c[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d %d %d", &a[i], &b[i], &c[i]);
    }

    int dp[MAX_N][3][3] = {{0}};
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < 3; j++) {
            for (int k = 0; k < 3; k++) {
                if (i == 0) {
                    dp[i][j][k] = (j == k) ? a[i] : INT_MIN;
                } else {
                    int max_val = INT_MIN;
                    for (int l = 0; l < 3; l++) {
                        if (l != k) {
                            max_val = std::max(max_val, dp[i - 1][l][k] + (j == l) ? a[i] : b[i]);
                        }
                    }
                    dp[i][j][k] = max_val;
                }
            }
        }
    }

    int max_val = INT_MIN;
    for (int i = 0; i < 3; i++) {
        max_val = std::max(max_val, dp[n - 1][i][i]);
    }

    printf("%d\n", max_val);

    return 0;
}
