#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> a(n), b(n), c(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i] >> b[i] >> c[i];
    }

    vector<vector<int>> dp(n, vector<int>(3, 0));
    dp[0][0] = a[0];
    dp[0][1] = b[0];
    dp[0][2] = c[0];

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < 3; j++) {
            int max_val = 0;
            for (int k = 0; k < 3; k++) {
                if (k != j) {
                    max_val = max(max_val, dp[i - 1][k]);
                }
            }
            dp[i][j] = max_val + (j == 0 ? a[i] : j == 1 ? b[i] : c[i]);
        }
    }

    int max_val = 0;
    for (int i = 0; i < 3; i++) {
        max_val = max(max_val, dp[n - 1][i]);
    }

    cout << max_val << endl;

    return 0;
}
