#include <stdio.h>

int knapsack(int n, int w, int weights[], int values[]) {
    int dp[n + 1][w + 1];

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            if (i == 0 || j == 0) {
                dp[i][j] = 0;
            } else if (weights[i - 1] <= j) {
                dp[i][j] = values[i - 1] + dp[i - 1][j - weights[i - 1]];
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }

    return dp[n][w];
}

int main() {
    int n, w;
    scanf("%d %d", &n, &w);

    int weights[n], values[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &weights[i], &values[i]);
    }

    int max_value = knapsack(n, w, weights, values);
    printf("%d\n", max_value);

    return 0;
}
