import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

public class Main {
    static int W, H, N;
    static int[][] enemy;
    static boolean[][] visited;
    static int[][] dist;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        W = Integer.parseInt(st.nextToken());
        H = Integer.parseInt(st.nextToken());
        N = Integer.parseInt(st.nextToken());
        enemy = new int[N][2];
        visited = new boolean[H][W];
        dist = new int[H][W];

        for (int i = 0; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            enemy[i][0] = Integer.parseInt(st.nextToken());
            enemy[i][1] = Integer.parseInt(st.nextToken());
        }

        int minCost = bfs();
        System.out.println(minCost);
    }

    private static int bfs() {
        int minCost = 0;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                dist[i][j] = Integer.MAX_VALUE;
            }
        }
        dist[0][0] = 0;

        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (dist[i][j] != Integer.MAX_VALUE) {
                    for (int k = 0; k < N; k++) {
                        if (enemy[k][0] == j && enemy[k][1] == i) {
                            visited[i][j] = true;
                        }
                    }
                    if (i > 0 && dist[i - 1][j] > dist[i][j] + 1) {
                        dist[i - 1][j] = dist[i][j] + 1;
                    }
                    if (j > 0 && dist[i][j - 1] > dist[i][j] + 1) {
                        dist[i][j - 1] = dist[i][j] + 1;
                    }
                    if (i < H - 1 && dist[i + 1][j] > dist[i][j] + 1) {
                        dist[i + 1][j] = dist[i][j] + 1;
                    }
                    if (j < W - 1 && dist[i][j + 1] > dist[i][j] + 1) {
                        dist[i][j + 1] = dist[i][j] + 1;
                    }
                }
            }
        }

        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (visited[i][j]) {
                    minCost += dist[i][j];
                }
            }
        }

        return minCost;
    }
}
