#include <stdio.h>
#include <string.h>

int main() {
    char s[3001], t[3001];
    int i, j, k, l, max_len = 0, max_idx = 0;

    scanf("%s %s", s, t);
    int n = strlen(s);
    int m = strlen(t);

    int dp[n + 1][m + 1];
    for (i = 0; i <= n; i++) {
        for (j = 0; j <= m; j++) {
            dp[i][j] = 0;
        }
    }

    for (i = 1; i <= n; i++) {
        for (j = 1; j <= m; j++) {
            if (s[i - 1] == t[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1] + 1;
                if (dp[i][j] > max_len) {
                    max_len = dp[i][j];
                    max_idx = i;
                }
            } else {
                dp[i][j] = 0;
            }
        }
    }

    for (i = max_len; i > 0; i--) {
        if (dp[max_idx][max_len] == i) {
            printf("%c", s[max_idx - 1]);
            max_idx--;
            max_len--;
        }
    }
    printf("\n");

    return 0;
}
