#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

int main() {
    std::string s, t;
    std::getline(std::cin, s);
    std::getline(std::cin, t);

    int m = s.size();
    int n = t.size();

    std::vector<int> dp(m + 1, 0);
    for (int i = 1; i <= m; i++) {
        int prev = dp[i - 1];
        for (int j = i; j <= m; j++) {
            if (s[i - 1] == s[j - 1]) {
                dp[j] = std::max(dp[j], prev + 1);
            }
            prev = std::max(prev, dp[i - 1]);
        }
    }

    std::vector<int> dp2(n + 1, 0);
    for (int i = 1; i <= n; i++) {
        int prev = dp2[i - 1];
        for (int j = i; j <= n; j++) {
            if (t[i - 1] == s[dp[j] - 1]) {
                dp2[j] = std::max(dp2[j], prev + 1);
            }
            prev = std::max(prev, dp2[i - 1]);
        }
    }

    int max_len = 0;
    int max_idx = 0;
    for (int i = 0; i <= n; i++) {
        if (dp2[i] > max_len) {
            max_len = dp2[i];
            max_idx = i;
        }
    }

    std::string res;
    for (int i = max_idx, j = max_len; i > 0 && j > 0; i--) {
        if (t[i - 1] == s[j - 1]) {
            res += t[i - 1];
            j--;
        }
    }

    std::reverse(res.begin(), res.end());
    std::cout << res << std::endl;

    return 0;
}
