#include <stdio.h>
#include <stdlib.h>

#define MAX_N 100005

int n, m, in[MAX_N], out[MAX_N], dp[MAX_N];
int vis[MAX_N], top;
int g[MAX_N][2];

void dfs(int u) {
    vis[u] = 1;
    for (int i = 0; i < 2; i++) {
        int v = g[u][i];
        if (!vis[v]) {
            dfs(v);
            dp[u] = dp[v] + 1;
        }
    }
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &g[i][0], &g[i][1]);
        in[g[i][1]]++;
        out[g[i][0]]++;
    }

    for (int i = 1; i <= n; i++) {
        if (!out[i]) {
            top++;
            dfs(i);
        }
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (dp[i] + in[i] + out[i] - 1 > ans) ? dp[i] + in[i] + out[i] - 1 : ans;
    }

    printf("%d\n", ans);
    return 0;
}
