#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 1e5 + 5;

int n, m;
vector<int> graph[MAX_N];
int inDegree[MAX_N];
int dp[MAX_N];

void topologicalSort() {
    queue<int> q;
    for (int i = 1; i <= n; i++) {
        if (inDegree[i] == 0) {
            q.push(i);
            dp[i] = 1;
        }
    }

    while (!q.empty()) {
        int node = q.front();
        q.pop();

        for (int neighbor : graph[node]) {
            inDegree[neighbor]--;
            dp[neighbor] = max(dp[neighbor], dp[node] + 1);
            if (inDegree[neighbor] == 0) {
                q.push(neighbor);
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v;
        graph[u].push_back(v);
        inDegree[v]++;
    }

    topologicalSort();
    int maxLength = 0;
    for (int i = 1; i <= n; i++) {
        maxLength = max(maxLength, dp[i]);
    }

    cout << maxLength << '\n';

    return 0;
}
