import java.io.*;
import java.util.*;

public class Main {
    static int N = (int) 1e5 + 5;
    static int M = (int) 1e5 + 5;
    static int[] h = new int[N];
    static int[] e = new int[M];
    static int[] ne = new int[M];
    static int[] w = new int[M];
    static int idx = 0;
    static int[] du = new int[N];
    static int[] f = new int[N];
    static int[] dep = new int[N];
    static int[] st = new int[N];
    static int top = 0;

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        for (int i = 0; i < m; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            add(a, b);
            du[b]++;
        }
        for (int i = 1; i <= n; i++) {
            if (du[i] == 0) {
                dfs(i, 0);
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            ans = Math.max(ans, f[i] + dep[i]);
        }
        System.out.println(ans);
    }

    static void add(int a, int b) {
        e[idx] = b;
        ne[idx] = h[a];
        w[idx] = 1;
        h[a] = idx++;
    }

    static void dfs(int u, int fa) {
        f[u] = f[fa] + 1;
        dep[u] = dep[fa] + 1;
        st[++top] = u;
        for (int i = h[u]; i != -1; i = ne[i]) {
            int j = e[i];
            if (j != fa) {
                dfs(j, u);
            }
        }
        if (f[u] > f[fa]) {
            while (st[top] != u) {
                f[st[top]] = f[st[top - 1]];
                top--;
            }
            f[u] = f[fa] + 1;
        }
        top--;
    }
}
