from collections import deque

def min_cost(W, H, N, enemies):
    visited = [[False] * H for _ in range(W)]
    queue = deque([(0, 0, 0, 0)])
    visited[0][0] = True

    while queue:
        x, y, cost, bombs = queue.popleft()

        if x == W - 1 and y == H - 1:
            return cost

        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy

            if 0 <= nx < W and 0 <= ny < H and not visited[nx][ny]:
                queue.append((nx, ny, cost + 1, bombs))
                visited[nx][ny] = True

        if (x, y) in enemies:
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy

                if 0 <= nx < W and 0 <= ny < H and (nx, ny) in enemies:
                    enemies.remove((nx, ny))
                    bombs += 1

            if not enemies:
                return cost + 1

    return -1

def main():
    W, H, N = map(int, input().split())
    enemies = set()
    for _ in range(N):
        x, y = map(int, input().split())
        enemies.add((x, y))

    print(min_cost(W, H, N, enemies))

if __name__ == "__main__":
    main()
