#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 300

double dp[N + 1][N + 1];

void solve() {
    int n;
    scanf("%d", &n);
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }
    dp[0][0] = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            if (j == 0) {
                dp[i][j] = 1;
            } else {
                for (int k = 1; k <= 3; k--) {
                    if (j - k >= 0) {
                        dp[i][j] += (double)1 / n * dp[i - 1][j - k];
                    }
                }
            }
        }
    }
    double ans = 0;
    for (int i = 1; i <= n; i++) {
        ans += dp[n][i] * i;
    }
    printf("%.10lf\n", ans);
}

int main() {
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
