import java.util.Scanner;

public class Main {
    static final int N = 300;
    static final double INF = 1e18;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        double[] f = new double[N + 1];
        double[] g = new double[N + 1];
        double[] h = new double[N + 1];

        for (int i = 1; i <= n; i++) {
            int a = scanner.nextInt();
            f[i] = g[i] = h[i] = 0;
            for (int j = 1; j <= a; j++) {
                f[i] += 1.0 / n;
                g[i] += (j + 1.0) / n;
                h[i] += (j + 1.0) * (j + 2.0) / (2.0 * n);
            }
        }

        for (int i = n; i >= 1; i--) {
            for (int j = i + 1; j <= n; j++) {
                f[i] += f[j] * (1.0 - g[i] / g[j]);
                g[i] += g[j] * (1.0 - g[i] / g[j]);
                h[i] += h[j] * (1.0 - g[i] / g[j]) + f[j] * (g[i] / g[j]) * (g[j] - g[i]);
                h[i] += f[j] * (g[i] / g[j]) * (g[j] - g[i]) * (j + 1.0);
            }
        }

        System.out.printf("%.10f\n", h[1]);
    }
}
